tabs.query:
PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>

SELECT ?element ?geometryDescription
WHERE{
	?element a bot:Element ;
		omg:hasSimpleGeometryDescription|omg:hasComplexGeometryDescription ?geometryDescription .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>

INSERT {
	?newElement a ?newElementType ;
		omg:hasSimpleGeometryDescription ?newDescription .
}
WHERE{
	BIND(ex:newColumn AS ?newElement)
	BIND(bot:Element AS ?newElementType)
	BIND("some new STEP geometry" AS ?newDescription)
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>

DELETE{?element omg:hasSimpleGeometryDescription ?oldDescription}
INSERT{?element omg:hasSimpleGeometryDescription ?newDescription}
WHERE{
	BIND("some changed STEP geometry!" AS ?newDescription)
	BIND(ex:newColumn AS ?element)
	?element omg:hasSimpleGeometryDescription ?oldDescription .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>

DELETE {?element omg:hasSimpleGeometryDescription ?geometryDescription}
WHERE{
  	BIND(ex:column1 AS ?element)
	?element omg:hasSimpleGeometryDescription ?geometryDescription .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>

CONSTRUCT
WHERE{
	# All bot:elements that have a simple geometry description connected to a geometry node
	?elementSimple a bot:Element ;
	 	omg:hasGeometry ?geometrySimple .
	?geometrySimple a omg:Geometry ; 
		omg:hasSimpleGeometryDescription ?simpleDescription .
	# All bot:elements that have a complex geometry description connected to a geometry node
	?elementComplex a bot:Element ;
	 	omg:hasGeometry ?geometryComplex .
	?geometryComplex a omg:Geometry ; 
		omg:hasComplexGeometryDescription ?complexDescription .
  	# in case of a complex geometry: give type of top node
	?complexDescription a ?dComplexType .
}

