PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>

INSERT{
	?object omg:hasGeometry ?newGeometry .
	?newGeometry omg:hasSimpleGeometryDescription ?newDescription ;
 		a omg:Geometry .
}
WHERE{
	BIND(ex:column3 AS ?object)
	BIND(ex:newGeometry AS ?newGeometry)
	BIND("some new STEP geometry" AS ?newDescription)
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>

DELETE {?geometry omg:hasSimpleGeometryDescription ?oldDescription}
INSERT {?geometry omg:hasSimpleGeometryDescription ?newDescription}
WHERE{
	BIND("some glTF json" AS ?oldDescription)
	BIND("some changed glTF json" AS ?newDescription)
	BIND(ex:column2 AS ?object)
	?object omg:hasGeometry ?geometry .
	?geometry omg:hasSimpleGeometryDescription ?oldDescription .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>

DELETE {
	?object omg:hasGeometry ?geometry .
  	?geometry omg:hasSimpleGeometryDescription ?oldDescription .
}
WHERE{
	BIND(ex:column3 AS ?object)
  	BIND(ex:geometry2 AS ?geometry)
	?object omg:hasGeometry ?geometry .
	?geometry omg:hasSimpleGeometryDescription ?oldDescription .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>

CONSTRUCT
WHERE{
	#All bot:elements that have a simple geometry description connected to a geometry node
	?elementSimple a bot:Element ;
	 	omg:hasGeometry ?geometrySimple .
	?geometrySimple a omg:Geometry ; 
		omg:hasSimpleGeometryDescription ?simpleDescription .
	#All bot:elements that have a complex geometry description connected to a geometry node
	?elementComplex a bot:Element ;
	 	omg:hasGeometry ?geometryComplex .
	?geometryComplex a omg:Geometry ; 
		omg:hasComplexGeometryDescription ?complexDescription .
	?complexDescription a ?dComplexType .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>

CONSTRUCT
WHERE{
	#Find all BOT elements and their connected geometries
	?element a bot:Element ;
		omg:hasGeometry ?geometrySimple ;
		omg:hasGeometry ?geometryComplex .
	#Find connected geometry states for "simple" geometry nodes
	?geometrySimple a ?gtypeSimple ;
		omg:hasGeometryState ?geometryStateSimple  .
	#Find the value of the geometry states' simple geometry descriptions
	?geometryStateSimple a ?gStatetypeSimple ;
		omg:hasSimpleGeometryDescription ?geometryDescriptionSimple .		
	#Find connected geometry states for "complex" geometry nodes
	?geometryComplex a ?gtypeComplex ;
		omg:hasGeometryState ?geometryStateComplex  .
	#Find the first node of the geometry states' complex geometry descriptions
	?geometryStateComplex a ?gStatetypeComplex ;
		omg:hasComplexGeometryDescription ?geometryDescriptionComplex .	
	#Find the type(s) of the complex geometry descriptions' first nodes.
	?geometryDescriptionComplex a ?dtype .
}

