PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>

SELECT ?element ?geometry ?geometryState ?geometryDescription
WHERE{
	?element omg:hasGeometry ?geometry .
	?geometry omg:hasGeometryState ?geometryState .
	?geometryState a omg:CurrentGeometryState ;
		(omg:hasComplexGeometryDescription|omg:hasSimpleGeometryDescription) ?geometryDescription .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX prov: <http://www.w3.org/ns/prov#>

INSERT{
	?element omg:hasGeometry ?newGeometry .
	?newGeometry a omg:Geometry ;
		omg:hasGeometryState ?newGeometryState .
	?newGeometryState a omg:CurrentGeometryState ;
		prov:generatedAtTime ?now ;
		omg:hasSimpleGeometryDescription ?newDescription .
}
WHERE{
	BIND(ex:column4 AS ?element)
	BIND(ex:newL3Geometry AS ?newGeometry)
	BIND(ex:newGeometryState AS ?newGeometryState)
	BIND(NOW() AS ?now) 
	BIND("some new STEP geometry" AS ?newDescription)
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX prov: <http://www.w3.org/ns/prov#>

DELETE{
	?oldGeometryState a omg:CurrentGeometryState .
}
INSERT{
	?geometry omg:hasGeometryState ?newGeometryState .
	?newGeometryState a omg:CurrentGeometryState ;
		prov:generatedAtTime ?now ;
		omg:hasSimpleGeometryDescription ?newDescription ;
		omg:hasContext ?context .
}
WHERE{
	BIND("some changed DWG geometry" AS ?newDescription)
	BIND(NOW() AS ?now) 
	BIND(ex:newGeometryStateSimple AS ?newGeometryState)
	BIND(ex:column4 AS ?element)
  	BIND(ex:geometry4 AS ?geometry)
	?element omg:hasGeometry ?geometry .
	?geometry omg:hasGeometryState ?oldGeometryState .
	?oldGeometryState omg:hasSimpleGeometryDescription ?oldDescription .
  	#optionally check if the geometry state is part of a omg:Context
	OPTIONAL { ?oldGeometryState omg:hasContext ?context }
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX prov: <http://www.w3.org/ns/prov#>
PREFIX opm: <https://w3id.org/opm#>
PREFIX schema: <http://schema.org/>

DELETE{
	?oldPropertyState a opm:CurrentPropertyState .
	?geometryState omg:isDerivedFromGeometryState ?otherGeometryState .
}
INSERT{
	?property opm:hasPropertyState ?newPropertyState .
	?newPropertyState a opm:CurrentPropertyState ;
       	schema:value ?newHeight ;
		prov:generatedAtTime ?now .
}
WHERE{
	BIND("600" AS ?newHeight)
	BIND(NOW() AS ?now) 
	BIND(ex:newPropertyState AS ?newPropertyState)
	BIND(ex:box1height AS ?property)
	?property opm:hasPropertyState ?oldPropertyState .
	?geometryState omg:containsPropertyState ?oldPropertyState .
	OPTIONAL{ ?geometryState omg:isDerivedFromGeometryState ?otherGeometryState . }
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX prov: <http://www.w3.org/ns/prov#>
PREFIX opm: <https://w3id.org/opm#>
PREFIX schema: <http://schema.org/>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>

DELETE{
	?oldGeometryState a omg:CurrentGeometryState .
}
INSERT{
	?geometry omg:hasGeometryState ?newGeometryState .
	?newGeometryState prov:generatedAtTime ?now ;
		omg:containsGeometryObject ?newGeometryObject ;
		omg:containsGeometryObject ?geometryObjects ;
		omg:hasComplexGeometryDescription ?newGeometryObject ;
		a omg:CurrentGeometryState ;
		omg:hasContext ?context .
	?newGeometryObject a ?geometryType;
		geometry:object ?geometryObject .
}
WHERE{
	BIND(ex:newTransformation AS ?newGeometryObject)
	BIND(ex:Collection1 AS ?geometryObject)
	BIND(NOW() AS ?now) 
	BIND(ex:newGeometryStateComplex AS ?newGeometryState)
	BIND(ex:column4 AS ?element)
	BIND(geometry:Transformation AS ?geometryType)
	?element omg:hasGeometry ?geometry .
	?geometry omg:hasGeometryState ?oldGeometryState .
	?oldGeometryState a omg:CurrentGeometryState ;
        #upper node of the geometry description
		omg:hasComplexGeometryDescription ?geometryObject ;
        #all nodes of the geometry description
		omg:containsGeometryObject ?geometryObjects ;
		omg:hasContext ?context .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX prov: <http://www.w3.org/ns/prov#>
PREFIX opm: <https://w3id.org/opm#>
PREFIX schema: <http://schema.org/>

DELETE{
	?oldGeometryState a omg:CurrentGeometryState .
}
INSERT{
	?geometry omg:hasGeometryState ?newGeometryState .
	?newGeometryState prov:generatedAtTime ?now ;
		a omg:CurrentGeometryState ;
		a opm:Deleted .
}
WHERE{
	BIND(NOW() AS ?now) 
	BIND(ex:newGeometryStateDeleted AS ?newGeometryState)
	BIND(ex:column4 AS ?element)
  	BIND(ex:geometry5 AS ?geometry)
	?element omg:hasGeometry ?geometry .
	?geometry omg:hasGeometryState ?oldGeometryState .
	?oldGeometryState a omg:CurrentGeometryState .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>

CONSTRUCT
WHERE{
	#Find all BOT elements and their connected geometries
	?element a bot:Element ;
		a ?type ;
		omg:hasGeometry ?geometrySimple ;
		omg:hasGeometry ?geometryComplex .
	#Find connected geometry states for "simple" geometry nodes
	?geometrySimple a ?gtypeSimple ;
		omg:hasGeometryState ?geometryStateSimple  .
	#Find the value of the geometry states' simple geometry descriptions
	?geometryStateSimple a ?gStatetypeSimple ;
		omg:hasSimpleGeometryDescription ?geometryDescriptionSimple .		
	#Find connected geometry states for "complex" geometry nodes
	?geometryComplex a ?gtypeComplex ;
		omg:hasGeometryState ?geometryStateComplex  .
	#Find the first node of the geometry states' complex geometry descriptions
	?geometryStateComplex a ?gStatetypeComplex ;
		omg:hasComplexGeometryDescription ?geometryDescriptionComplex .	
	#Find the type of the geometry descriptions' first nodes.
	?geometryDescriptionComplex a ?dtype .
	#Find elements with deleted geometry states
	?dElement a bot:Element ;
		omg:hasGeometry ?dGeometry .
	?dGeometry a ?dGtype ;
		omg:hasGeometryState ?deletedState .
	?deletedState a ?dStype .
}

