PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>

SELECT ?element ?geometry ?geometryState ?geometryDescription ?context
WHERE{
	?element a bot:Element ;
		omg:hasGeometry ?geometry .
	{
    	# geometry L2
		?geometry omg:hasContext ?context ;
			omg:hasComplexGeometryDescription|omg:hasSimpleGeometryDescription ?geometryDescription .
	}UNION{
      	# geometry L3
		?geometry omg:hasGeometryState ?geometryState .
		?geometryState a omg:CurrentGeometryState ;
			omg:hasContext ?context ;
			omg:hasComplexGeometryDescription|omg:hasSimpleGeometryDescription ?geometryDescription .
	}
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>

DELETE{
	?oldContext ?p ?o .
	?s ?pp ?oldContext .
}
WHERE{
	BIND(ex:highDetail AS ?oldContext)
	?oldContext ?p ?o .
	?s ?pp ?oldContext .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>

CONSTRUCT
WHERE{
	#The transformed geometry, its element and its geometry description
	?elementTransform a bot:Element	;
		omg:hasGeometry ?geometryTransform .
	?geometryTransform a omg:Geometry ;
		omg:transformsGeometry ?originGeometryTransform ;
		omg:hasComplexGeometryDescription ?descriptionTransform .
	?descriptionTransform a ?dtype .
	#The origin geometry of the transformation and its element
	?elementOriginTransform a bot:Element ;
		omg:hasGeometry ?originGeometryTransform .
	#The complementing geometry and its element
	?elementComplement a bot:Element ;
		omg:hasGeometry ?geometryComplement .
	?geometryComplement a omg:Geometry ;
		omg:complementsGeometry ?originGeometryComplement .
	#The origin geometry of the complementation and its element
	?elementOriginComplement a bot:Element ;
		omg:hasGeometry ?originGeometryComplement .
}

