tabs.query:
PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>

SELECT ?geometry ?typeOfRelation ?baseGeometry
WHERE{
	?geometry a omg:Geometry ;
		omg:complementsGeometry|omg:transformsGeometry ?baseGeometry ;
		?typeOfRelation ?baseGeometry .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>

INSERT{
	?complements omg:complementsGeometry ?origin .
}
WHERE{
	BIND(ex:geometry7 AS ?origin)
	BIND(ex:geometry5 AS ?complements)
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>

DELETE{
	?complements omg:complementsGeometry ?origin .
}
WHERE{
	BIND(ex:geometry5 AS ?origin)
	BIND(ex:geometry4 AS ?complements)
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>

CONSTRUCT
WHERE{
	#The transformed geometry, its element and its geometry description
	?elementTransform a bot:Element	;
		omg:hasGeometry ?geometryTransform .
	?geometryTransform a omg:Geometry ;
		omg:transformsGeometry ?originGeometryTransform ;
		omg:hasComplexGeometryDescription ?descriptionTransform .
	?descriptionTransform a ?dtype .
	#The origin geometry of the transformation and its element
	?elementOriginTransform a bot:Element ;
		omg:hasGeometry ?originGeometryTransform .
	#The complementing geometry and its element
	?elementComplement a bot:Element ;
		omg:hasGeometry ?geometryComplement .
	?geometryComplement a omg:Geometry ;
		omg:complementsGeometry ?originGeometryComplement .
	#The origin geometry of the complementation and its element
	?elementOriginComplement a bot:Element ;
		omg:hasGeometry ?originGeometryComplement .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>
PREFIX seas: <https://w3id.org/seas/>
PREFIX opm: <https://w3id.org/opm#>
PREFIX schema: <http://schema.org/>

CONSTRUCT
WHERE{
	#bot:Element with properties and geometries with implicit and explicit derivation
	?elementImplicit a bot:Element	;
		omg:hasGeometry ?geometryImplicit ;
		omg:hasGeometry ?geometryExplicit ;
		seas:hasProperty ?propertyImplicit ;
		seas:hasProperty ?propertyExplicit .
	#Implicit derivation of a property
	?geometryImplicit a omg:Geometry .
	?propertyImplicit omg:isImplicitlyDerivedFrom ?geometryImplicit .
	#Explicit derivation of a property 
	?geometryExplicit omg:hasGeometryState ?geometryState .
	?geometryState a omg:CurrentGeometryState ;
		omg:containsGeometryObject ?geometryObject ;
		omg:containsPropertyState ?geometricPropertyState .
	?geometricObject seas:hasProperty ?geometricProperty .
    ?geometricProperty opm:hasPropertyState ?geometricPropertyState .
	?propertyExplicit omg:isExplicitlyDerivedFrom ?geometricProperty ;
		omg:hasInferredPropertyState ?geometricPropertyState .
}

