PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>
PREFIX seas: <https://w3id.org/seas/>
PREFIX opm: <https://w3id.org/opm#>
PREFIX prov: <http://www.w3.org/ns/prov#>
PREFIX schema: <http://schema.org/>

SELECT ?outdatedGeometry ?originGeometry
WHERE{
	#define outdated geometries and geometry states as well as the changed geometry state
	?outdatedGeometry omg:hasGeometryState ?geometryState .
	?changedGeometryState a omg:CurrentGeometryState .
	?geometryState a omg:CurrentGeometryState .
	{
		#any potentially changed geometry state with it's derived geometry states. In case the first geometry state has actually been changed, its timestamp will be newer than the one of the derived geometry state, thus pointing out data discrepancies
		?changedGeometryState ^omg:isDerivedFromGeometryState ?geometryState .		
	}UNION{
		#in case a geometry is changed, either a new geometry state is introduced or the omg:isDerivedFromGeometryState relation is removed for the changed geometry state. Thus, if a geometry state is not derived from another geometry even though it should be, it was modified and other geometry states are outdated.
		?changedGeometry omg:hasGeometryState ?changedGeometryState ;
			omg:isDerivedFromGeometry ?outdatedGeometry .
		FILTER NOT EXISTS {?changedGeometryState omg:isDerivedFromGeometryState ?geometryState}
	}
	{
		#Find the current (MAX) timestamp of every potentially changed geometry state (considering both geometry state and contained property states)
		SELECT ?changedGeometryState (MAX(?time) AS ?changeTimestamp)
		WHERE{
			{
				#timestamp of geometry state
				?changedGeometryState prov:generatedAtTime ?time .
			}UNION{
				#timestamp of contained property states
				?changedGeometryState omg:containsPropertyState ?propertyState .
				?propertyState prov:generatedAtTime ?time
			}
		}GROUP BY ?changedGeometryState
	}
	{
		#Find the current (MAX) timestamp of every potentially outdated geometry state (considering both geometry state and contained property states)
		SELECT ?geometryState (MAX(?time) AS ?outdatedTimestamp)
		WHERE{
			{
				#timestamp of geometry state
				?geometryState prov:generatedAtTime ?time .
			}UNION{
				#timestamp of contained property states
				?geometryState omg:containsPropertyState ?propertyState .
				?propertyState prov:generatedAtTime ?time
			}
		}GROUP BY (?geometryState)
	}
	FILTER(?changeTimestamp>?outdatedTimestamp)
	?outdatedGeometry omg:isDerivedFromGeometry ?originGeometry .
}

PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>
PREFIX seas: <https://w3id.org/seas/>
PREFIX opm: <https://w3id.org/opm#>
PREFIX schema: <http://schema.org/>
PREFIX prov: <http://www.w3.org/ns/prov#>

DELETE{
	?oldPropertyState a opm:CurrentPropertyState .
	?geometryState omg:isDerivedFromGeometryState ?otherGeometryState .
}
INSERT{
	?property opm:hasPropertyState ?newPropertyState .
	?newPropertyState a opm:CurrentPropertyState ;
		schema:value ?newHeight ;
		prov:generatedAtTime ?now .
}
WHERE{
	BIND("600" AS ?newHeight)
	BIND(NOW() AS ?now) 
	BIND(ex:newPropertyState AS ?newPropertyState)
	BIND(ex:box1height AS ?property)
	?property opm:hasPropertyState ?oldPropertyState .
	?geometryState omg:containsPropertyState ?oldPropertyState .
	OPTIONAL{ ?geometryState omg:isDerivedFromGeometryState ?otherGeometryState . }
}

