PREFIX bot: <https://w3id.org/bot#>
PREFIX omg: <https://w3id.org/omg#>
PREFIX ex: <http://example.org/columns#>
PREFIX geometry: <http://rdf.bg/geometry.ttl#>
PREFIX seas: <https://w3id.org/seas/>
PREFIX opm: <https://w3id.org/opm#>
PREFIX schema: <http://schema.org/>
PREFIX prov: <http://www.w3.org/ns/prov#>

CONSTRUCT
WHERE{
	#Geometries that can be derived and have geometry states that were derived
	?geometryIsDerived a omg:Geometry ;
		omg:hasGeometryState ?geometryStateIsDerived ; 
		omg:isDerivedFromGeometry ?isDerivedFromGeometry .
	?geometryStateIsDerived a omg:CurrentGeometryState ;
		omg:isDerivedFromGeometryState ?isDerivedFromGeometryState .
	#Geometries that can be derived but have no geometry states that were derived
	?geometry a omg:Geometry ;
		omg:hasGeometryState ?geometryState ;
		omg:isDerivedFromGeometry ?derivedFromGeometry .
	?geometryState a omg:CurrentGeometryState .
}
